mapper.update:
UPDATE NOTICES
SET NOTICE_TYPE_ID=#{notice_type_id}, SUBJECT=#{subject}, CONTENT=#{content}
WHERE NOTICE_ID=#{notice_id};

mapper.select:
SELECT N.NOTICE_ID, T.TYPE, N.SUBJECT, N.CONTENT, N.HITCOUNT, N.NOTICED_DATE FROM NOTICES N JOIN NOTICE_TYPE T ON N.NOTICE_TYPE_ID = T.NOTICE_TYPE_ID
AND N.SUBJECT LIKE '%' || #{subject} || '%'
AND N.NOTICE_TYPE_ID = #{notice_type_id}

mapper.update:
UPDATE NOTICES
SET HITCOUNT = HITCOUNT + 1
WHERE NOTICE_ID = #{notice_id}
